import React, { useEffect } from "react";
import './App.css';
// Import removed since we're using background-image in CSS

export default function App() {
  useEffect(() => {
    // Handle smooth scrolling for navigation links
    const navLinks = document.querySelectorAll('.nav-link, .button');
    const navContainer = document.querySelector('.nav-container');

    // Function to handle scroll event
    const handleScroll = () => {
      // Add class to navigation when scrolling down
      if (window.scrollY > 100) {
        navContainer.classList.add('scrolled');
      } else {
        navContainer.classList.remove('scrolled');
      }

      // Highlight the active section in the navigation
      const sections = document.querySelectorAll('section');
      const scrollPosition = window.scrollY + 150;

      sections.forEach(section => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.offsetHeight;
        const sectionId = section.getAttribute('id');

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
          document.querySelectorAll('.nav-link').forEach(link => {
            link.classList.remove('active');
            if (link.getAttribute('href') === `#${sectionId}`) {
              link.classList.add('active');
            }
          });
        }
      });
    };

    // Add scroll event listener
    window.addEventListener('scroll', handleScroll);

    navLinks.forEach(link => {
      link.addEventListener('click', function(e) {
        // Only apply to links that point to an anchor
        const href = this.getAttribute('href');
        if (href && href.startsWith('#') && href !== '#') {
          e.preventDefault();

          const targetId = this.getAttribute('href');
          const targetElement = document.querySelector(targetId);

          if (targetElement) {
            window.scrollTo({
              top: targetElement.offsetTop - 80, // Offset for the navigation bar
              behavior: 'smooth'
            });

            // Update URL without triggering page reload
            window.history.pushState(null, null, targetId);

            // Set this link as active
            navLinks.forEach(link => link.classList.remove('active'));
            this.classList.add('active');
          }
        }
      });
    });

    // Handle the case when the page is loaded with a hash
    if (window.location.hash) {
      setTimeout(() => {
        const element = document.querySelector(window.location.hash);
        if (element) {
          window.scrollTo({
            top: element.offsetTop - 80,
            behavior: 'smooth'
          });

          // Set the corresponding link as active
          const activeLink = document.querySelector(`.nav-link[href="${window.location.hash}"]`);
          if (activeLink) {
            navLinks.forEach(link => link.classList.remove('active'));
            activeLink.classList.add('active');
          }
        }
      }, 100);
    }

    // Initial call to set the correct state based on current scroll position
    handleScroll();

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      {/* Background container with image */}
      <div className="bg-image-container">
        <div className="bg-image updated-bg" style={{ margin: '0 auto' }}></div>
      </div>

      {/* Navigation */}
      <nav className="nav-container">
        <a href="#home" className="nav-link">Home</a>
        <a href="#about" className="nav-link">About</a>
        <a href="#tokenomics" className="nav-link">Tokenomics</a>
        <a href="#nfts" className="nav-link">NFTs</a>
        <a href="#whitepaper" className="nav-link">Whitepaper</a>
      </nav>

      {/* Content */}
      <div className="content-container">
        <section id="home" className="hero">
          <h1>BHABIT</h1>
          <p>Embrace Your Bad Habits</p>
          <p className="subtitle">A Coin for the Rebels. A Token for the Bad Decisions.</p>
          <a href="#about" className="button">Get Your BHABIT</a>
        </section>

        <section id="about" className="content-section">
          <div className="section-container">
            <h2 className="glitch-text">What is BHABIT?</h2>
            <p>BHABIT is the cryptocurrency for those who embrace bad habits and laugh at convention. With an infinite supply of rebellious energy and a total of 13 trillion coins, we're here to prove that you don't need to be perfect to thrive — sometimes, bad habits are the best kind of habits.</p>
            <p>In a world where good habits are overrated, BHABIT dares to stand out.</p>
          </div>
        </section>

        <section id="tokenomics" className="content-section">
          <div className="section-container bg-black">
            <div className="number-13">13</div>
            <h2 className="glitch-text">The Coin</h2>
            <p>There are 13 trillion BHABIT coins. Why? Because why not. We embrace chaos, abundance, and the beauty of imperfection.</p>
            <div className="grid grid-2">
              <div className="card">
                <h3>Total Supply</h3>
                <p className="token-number">13 trillion</p>
              </div>
              <div className="card">
                <h3>Burn Mechanism</h3>
                <p>13% of each transaction is burned or redistributed</p>
              </div>
            </div>
            <a href="#nfts" className="button">Explore NFTs</a>
          </div>
        </section>

        <section id="nfts" className="content-section">
          <div className="section-container bg-gradient">
            <h2 className="glitch-text">Bad Habits Never Die: Collect Your BHABIT NFTs</h2>
            <p>For every bad habit you own, there's an NFT waiting for you. Get your 13 most rebellious NFTs today.</p>
            <div className="grid grid-3">
              <div className="nft-card card">
                <h3>Procrastination King</h3>
                <div className="nft-placeholder"></div>
              </div>
              <div className="nft-card card">
                <h3>Junk Food Junkie</h3>
                <div className="nft-placeholder"></div>
              </div>
              <div className="nft-card card">
                <h3>Sleep Depriver</h3>
                <div className="nft-placeholder"></div>
              </div>
            </div>
            <a href="#whitepaper" className="button">Read Whitepaper</a>
          </div>
        </section>

        <section id="whitepaper" className="content-section">
          <div className="section-container">
            <h2 className="glitch-text">Whitepaper</h2>
            <p>The BHABIT whitepaper outlines our vision, tokenomics, and roadmap for the future.</p>
            <div className="card">
              <h3>Key Highlights</h3>
              <ul className="feature-list">
                <li>13 trillion total supply</li>
                <li>Decentralized governance</li>
                <li>Community-driven development</li>
                <li>Revolutionary NFT collection</li>
              </ul>
            </div>
            <a href="#home" className="button">Back to Top</a>
          </div>
        </section>

        <footer>
          <p>© 2025 BHABIT. All rights reserved.</p>
          <div className="footer-links">
            <a href="#" className="footer-link">Privacy Policy</a>
            <a href="#" className="footer-link">Terms & Conditions</a>
            <a href="#" className="footer-link">Contact Us</a>
          </div>
        </footer>
      </div>
    </>
  );
}
